mapper.select:
select IFNULL(SUM(ao.pay_money),0) FROM apply_order as ao LEFT JOIN match_data as md ON md.id = ao.match_id WHERE pay_statue = 1
and ao.user_sign_status > #{signState,jdbcType=INTEGER}
and ao.match_id = #{matchId,jdbcType=VARCHAR}
and md.user_id = #{userId,jdbcType=VARCHAR}
and md.add_time like CONCAT('%',#{today,jdbcType=VARCHAR},'%')

SELECT IFNULL(SUM(ao.has_partner + 1),0) FROM apply_order as ao LEFT JOIN match_data as md ON md.id = ao.match_id WHERE pay_statue = 1
and md.user_id = #{userId,jdbcType=VARCHAR}
and ao.match_id = #{matchId,jdbcType=VARCHAR}
and md.add_time like CONCAT('%',#{today,jdbcType=VARCHAR},'%')
