mapper.select:
select id, md.user_id, theme, start_date, end_date, province, city, area, address, contact_name, phone, wx_head, fee, banner_img, add_time, is_delete, fee_share_percent, statue, remark, order_no,su.user_account as operate_user from match_data as md left join sys_user as su on su.user_id = md.operate_user where 1=1
and statue= #{statue,jdbcType=INTEGER}
and theme like CONCAT('%',#{theme,jdbcType=VARCHAR},'%')
and md.user_id= #{userId,jdbcType=VARCHAR}
order by add_time DESC

select count(id) from match_data where 1=1
and statue= #{statue,jdbcType=INTEGER}
and theme like CONCAT('%',#{theme,jdbcType=VARCHAR},'%')
